$(function(){if("index.html"===window.location.href.split("/").pop())new Swiper(".swiper-container",{pagination:{el:".swiper-pagination",type:"fraction"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});else"shop.html"===window.location.href.split("/").pop()&&$("#sort_by").on("click",()=>{$("#sort_by").hasClass("hidden")?($("#sort_by").removeClass("hidden"),$("#region_list").removeClass("hidden")):($("#sort_by").addClass("hidden"),$("#region_list").addClass("hidden"))});$("#down_scroll").on("click",()=>{let i=$(window).height()+149;$("html, body").animate({scrollTop:i},{duration:800,easing:"swing"})}),$("#scroll_to_top").on("click",()=>{$("html, body").animate({scrollTop:0},{duration:800,easing:"swing"})})});